// API Configuration
export const API_BASE_URL = process.env.NODE_ENV === 'production' 
  ? (process.env.REACT_APP_API_URL || 'https://lyfe-production.up.railway.app')
  : 'http://localhost:5002';

// Debug logging for all environments
console.log('üåê API Configuration Debug:');
console.log('üåê NODE_ENV:', process.env.NODE_ENV);
console.log('üåê REACT_APP_API_URL:', process.env.REACT_APP_API_URL);
console.log('üåê API_BASE_URL:', API_BASE_URL);

// Helper function to build API URLs
export const buildApiUrl = (endpoint) => {
  const url = `${API_BASE_URL}${endpoint}`;
  console.log('üåê Building API URL:', url);
  return url;
};
